import React, { useState, useEffect, useContext } from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios';

// Import assets
import DetailIcon from "../assets/shared/search-icon-blue.png"

const Dashboard = () => {

    const [results, setResults] = useState([]);
    const [table, setTable] = useState("");
    const [filter, setFilter] = useState("");
    const [value, setValue] = useState("");
    const[filterOptions, setFilterOptions] = useState([]);

    // Store table content
    const [labos,setLabos] = useState([]);
    const [experiments, setExperiments] = useState([]);
    const [employees, setEmployees] = useState([]);
    const [projects, setProjects] = useState([]);


    const employeeHeader = ["Name", "Surname", "Phone Number", "Email", "Laboratory"];
    const laboratoryHeader = ["Name", "City", "Phone Number", "Email", "Number of Emp."];
    const projectHeader = ["Subject", "Description", "Status", "Publication Nb.", "Start Date"];

    // Get all tables
    // Get all tables
    useEffect(() => {
        const fetchData = async () => {
            try {
                const resLabos = await axios.get('/api/labos');
                setLabos(resLabos.data);

                const resExperiments = await axios.get('/api/experiments');
                setExperiments(resExperiments.data);

                const resEmployees = await axios.get('/api/employees');
                setEmployees(resEmployees.data);

                const resProjects = await axios.get('/api/projects');
                setProjects(resProjects.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, []);

    const handleTableChange = (e) => {
        // Update the table state when the selected value changes
        const selectedTable = e.target.value;
        console.log('Selected Table:', selectedTable);
    
        let filterOptions = [];
    
        switch (selectedTable) {
            case 'laboratory':
                filterOptions = ['name', 'address', 'city'];
                break;
            case 'employee':
                filterOptions = ['name', 'surname'];
                break;
            case 'project':
                filterOptions = ['name', 'description', 'status'];
                break;
            // Add more cases for other tables if needed
            default:
                filterOptions = [];
        }
    
        console.log('Filter Options:', filterOptions);
    
        setFilterOptions(filterOptions);
        setFilter(filterOptions[0]);
    };
    

    const handleFilterChange = (e) => {
        // Update the table state when the selected value changes
        setFilter(e.target.value);
    };

    const handleValueChange = (e) => {
        // Update the table state when the selected value changes
        setValue(e.target.value);
    };

    const handleSearch = () => {
        // Implement the logic to fetch data based on the selected table, filter, and value
        // For simplicity, I'll use the laboratory data as an example. Adjust it based on your actual API structure.
    
        let searchData = [];
    
        switch (table) {
            case 'laboratory':
                searchData = labos.filter((labo) => {
                    // Use the selected filter and value for filtering
                    return labo[filter].toLowerCase().includes(value.toLowerCase());
                });
                break;
            case 'employee':
                // Implement similar logic for employee data
                break;
            case 'project':
                // Implement similar logic for project data
                break;
            // Add more cases for other tables if needed
            default:
                searchData = [];
        }
    
        setResults(searchData);
    };

  return (
    <div className='dashboard-container'>
       <h1 className='page-title'>Database Request</h1>

       <section className='dashboard-navigation'>
            <Link to='/dashboard' className='navbar-link'>Database request</Link>
            <Link to='/ressources' className='navbar-link'>Global Ressources</Link>
            <Link to='/projects' className='navbar-link'>Projects</Link>
            <Link to='/inventory' className='navbar-link'>Inventory</Link>
            <Link to='/reservation' className='navbar-link'>Reservation</Link>
       </section>

       <section className='panels-container'>

            <div className='user-panel'>
                <h2 className='user-panel-title'>Your Panel</h2>

                <div className='information-container'>
                    <p className='next-meeting'><span>Next Meeting:</span> 06/11/2023</p>
                    <p className='reservation'><span>Reservation:</span> NaN</p>
                    <p className='mail-box'><span>Mailbox:</span> 1 Message</p>
                </div>
            </div>

            <div className='search-container'>

                <h2>Search form</h2>

                <p className='search-guide'>
                    The dashboard search form empowers users to seamlessly explore multiple database tables. By selecting a table and entering a search term, users can swiftly 
                    retrieve relevant information. The intuitive interface and dynamic updates enhance the search experience, offering a user-friendly gateway
                    to explore and interact with diverse datasets. 
                </p>

                <form className='search-form'>

                    <div className='input-container'>
                        <label for="tableName">Select a search topic:</label>
                        <select id="tableName" name="tableName" className='input-field' onChange={handleTableChange}>
                            <option value="laboratory">Laboratory</option>
                            <option value="employee">Employee</option>
                            <option value="project">Project</option>
                            <option value="experiment">Experiment</option>
                        </select>                    
                    </div>


                    <div className='input-container'>
                        <label for="filterName">Filter by:</label>
                        <select id="filterName" name="filterName" className='input-field' onChange={handleFilterChange}>
                        {filterOptions.map((option) => (
                            <option key={option} value={option}>
                                {option.charAt(0).toUpperCase() + option.slice(1)}
                            </option>
                        ))}
                        </select>                    
                    </div>

                    <div className='input-container'>
                        <label for="valueName">Value:</label>
                        <input type='text' name="filterValue" className='input-field' onChange={handleValueChange}></input>                    
                    </div>

                    <div className='buttons-container'>
                        <button className='search-btn' type="button" onClick={handleSearch}>Search</button>
                    </div>

                </form>
            </div> 

       </section>
       <div className='results-table-container'>
            <h2 className='results-table-title'>Results</h2>
            <div className='table'>
                <div className='table-header'>
                    {/* Load correct header here based on the selected table */}
                    {table === 'laboratory' && laboratoryHeader.map((header, index) => (
                        <p key={index} className='column-header' id={`${header.toLowerCase()}-header`}>
                            {header}
                        </p>
                    ))}
                    {table === 'employee' && employeeHeader.map((header, index) => (
                        <p key={index} className='column-header' id={`${header.toLowerCase()}-header`}>
                            {header}
                        </p>
                    ))}
                    {table === 'project' && projectHeader.map((header, index) => (
                        <p key={index} className='column-header' id={`${header.toLowerCase()}-header`}>
                            {header}
                        </p>
                    ))}
                    {/* Add more cases for other tables if needed */}
                </div>
                <div className='table-content'>
                    {results.map((result) => (
                        <div key={result.id} className='table-row'>
                            {/* Load data based on the selected table */}
                            {table === 'laboratory' && (
                                <>
                                    <p className={result.name ? '' : 'nan-value'} id='name-cell'>{result.name ? result.name : "NaN"}</p>
                                    <p className={result.city ? '' : 'nan-value'} id='city-cell'>{result.city ? result.city : "NaN"}</p>
                                    {/* Add more fields for laboratory table */}
                                </>
                            )}
                            {table === 'employee' && (
                                <>
                                    <p className={result.name ? '' : 'nan-value'} id='name-cell'>{result.name ? result.name : "NaN"}</p>
                                    <p className={result.surname ? '' : 'nan-value'} id='surname-cell'>{result.surname ? result.surname : "NaN"}</p>
                                    {/* Add more fields for employee table */}
                                </>
                            )}
                            {table === 'project' && (
                                <>
                                    <p className={result.name ? '' : 'nan-value'} id='name-cell'>{result.name ? result.name : "NaN"}</p>
                                    <p className={result.description ? '' : 'nan-value'} id='description-cell'>{result.description ? result.description : "NaN"}</p>
                                    {/* Add more fields for project table */}
                                </>
                            )}
                            {/* Add more cases for other tables if needed */}
                            <Link className="link" to={`/${table}/${result.id}`}>
                                <img className='detail-icon' src={DetailIcon} alt="view detail"></img>
                            </Link>
                        </div>
                    ))}
                </div>
            </div>
        </div>


       
    </div>
  )
}

export default Dashboard
