import React from 'react'
import {useState, useEffect, useContext} from 'react'
import { Link, useLocation, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/authContext";
import axios from 'axios';



const Employee = () => {

  const [employee, setEmployee] = useState({});

  const location = useLocation();
  const navigate = useNavigate();

  // Get the employee Id
  const employeeId = location.pathname.split("/")[2];

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/api/employees/${employeeId}`);
        console.log(res.data)
        setEmployee(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [employeeId]);


  return (
    <div className='employee-page'>
      <h1 className='employee-page-title'>Employee details</h1>

      <div className='employee-details-container'>
        <h2 className='employee-name'>{employee.name ? employee.name : "Empty"}</h2>




        <div className='details'>

          <div className='subcontainer'>
            <div className='detail'>
              <p className='detail-label'>Name:</p>
              <p className='detail-value'>{employee.name}</p>
            </div>

            <div className='detail'>
              <p className='detail-label'>Surname:</p>
              <p className='detail-value'>{employee.speciality}</p>
            </div>

            <div className='detail'>
              <p className='detail-label'>Address:</p>
              <p className='detail-value'>{employee.address}</p>
            </div>

            <div className='detail'>
              <p className='detail-label'>City:</p>
              <p className='detail-value'>{employee.city}</p>
            </div>
          </div>

          <div className='subcontainer'>
            <div className='detail'>
              <p className='detail-label'>Phone number:</p>
              <p className='detail-value'>{employee.phoneNumber}</p>
            </div>

            <div className='detail'>
              <p className='detail-label'>Email:</p>
              <p className='detail-value'>{employee.email}</p>
            </div>

            <div className='detail'>
              <p className='detail-label'>Website:</p>
              <p className='detail-value'>{employee.website}</p>
            </div>

            <div className='detail'>
              <p className='detail-label'>Number of Emp:</p>
              <p className='detail-value'>{employee.headcount}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Employee
